if (typeof o === 'string') {
  try { o = JSON.parse(o); } catch (e) { o = {}; }
}

return [{
  json: {
    segment: String(o.segment || ""),
    entity_type: String(o.entity_type || ""),
    is_tool: Boolean(o.is_tool),
    tool_name: String(o.tool_name || ""),
    website: String(o.website || ""),
    summary: String(o.summary || ""),
    key_use_case: String(o.key_use_case || ""),
    target_user: String(o.target_user || ""),
    funding_stage: String(o.funding_stage || ""),
    main_source_url: String(o.main_source_url || ""),
    score_relevance: Number(o.score_relevance || 0),
    score_confidence: Number(o.score_confidence || 0),
    tags: String(o.tags || "")
  }
}];

const seen = new Set();
const out = [];

for (const item of items) {
const link = (item.json.link || item.json.url || "").toString().toLowerCase().trim();
const title = (item.json.title || "").toString().toLowerCase().trim();

const key = link || title;

if (!key) {
out.push(item);
continue;
}

if (seen.has(key)) continue;
seen.add(key);

out.push(item);
}

return out;

const out = [];

for (const item of items) {
const j = item.json || {};
const seg = j.segments || {};

const segment = seg.segment || "";
const geography = "UK";

const organic = j.body?.organic_results || j.organic_results || [];
const evidence = Array.isArray(organic)
? organic.slice(0, 8).map(r => ({
title: r?.title || "",
snippet: r?.snippet || "",
link: r?.link || ""
}))
: [];

out.push({
json: {
segment,
geography,
evidence
}
});
}

return out;

values.content:
Analyze this evidence and return structured insights.

Input:
segment: {{ $json.segment }}
geography: {{ $json.geography }}
evidence: {{ JSON.stringify($json.evidence) }}

Return ONLY this JSON structure (NO markdown, NO code blocks):
{
"segment": "<segment name>",
"geography": "<geography>",
"executive_summary": "<1-2 sentence overview>",
"why_it_matters": "<2-3 sentences on significance>",
"key_trends": "<newline-separated phrases, 3-6 items>",
"key_use_cases": "<newline-separated phrases, 3-6 items>",
"notable_tools": "<comma-separated product names found in evidence>",
"example_sources": "<newline-separated URLs from evidence>"
}

