// Extract from various possible LLM output structures
let raw = $json.output ?? $json.content?.[0]?.text ?? $json.text ?? $json;
let o = raw;

// If it's a string, try to parse it
if (typeof o === 'string') {
  try { o = JSON.parse(o); } catch (e) { o = {}; }
}

return [{
  json: {
    segment: String(o.segment || ""),
    geography: String(o.geography || ""),
    executive_summary: String(o.executive_summary || ""),
    why_it_matters: String(o.why_it_matters || ""),
    key_trends: String(o.key_trends || ""),
    key_use_cases: String(o.key_use_cases || ""),
    notable_tools: String(o.notable_tools || ""),
    example_sources: String(o.example_sources || "")
  }
}];

const tools = $input.all().map(item => {
  const props = item.json.properties;
  return {
    tool_name: props.Name?.title[0]?.plain_text || "Untitled",
    segment: props.Segment?.select?.name || "unknown",
    geography: props.Geography?.select?.name || "UK",
    summary: props.Summary?.rich_text[0]?.plain_text || "",
    status: props.Status?.select?.name || "unknown",
    relevance: props.Relevance?.number || 0,
    change_log: props['Change Log']?.rich_text[0]?.plain_text || ""
  };
});

const grouped = {
  "BrokerTech": tools.filter(t => t.segment === "broker tech"),
  "EA Tech": tools.filter(t => t.segment === "ea tech"),
  "ConveyTech": tools.filter(t => t.segment === "convey tech"),
  "LenderTech": tools.filter(t => t.segment === "lender tech"),
  "Global": tools.filter(t => t.geography === "Global")
};

return [{ json: { grouped, total: tools.length } }];

