const data = items[0]?.json || {};
const store = this.getWorkflowStaticData('global');
store.agentPersona = data.agentPersona;
store.objectives = data.objectives;
store.reportingEmail = data.reportingEmail;
return items;

const raw = items[0]?.json?.choices?.[0]?.message?.content ?? '{}';
let payload;
try {
  payload = typeof raw === 'string' ? JSON.parse(raw) : raw;
} catch (error) {
  throw new Error('Unable to parse agent plan JSON: ' + error.message + ' -> ' + raw);
}
const tasks = payload.tasks ?? [];
return tasks.map(task => ({ json: { task } }));

const map = new Map();
for (const item of items) {
  const task = item.json.task;
  const domain = (task.domain || 'utility').toLowerCase();
  if (!map.has(domain)) {
    map.set(domain, []);
  }
  map.get(domain).push(task);
}
const globalData = this.getWorkflowStaticData('global');
globalData.taskMap = {};
globalData.results = [];
for (const [domain, list] of map.entries()) {
  globalData.taskMap[domain] = list;
}
const domains = ['calendar','drive','gmail','linkedin','twitter','utility'];
return domains.map(domain => ({ json: { domain, tasks: globalData.taskMap[domain] || [] } }));

const globalData = this.getWorkflowStaticData('global');
const results = globalData.results || [];
return [{ json: { results } }];

const globalData = this.getWorkflowStaticData('global');
const results = items[0].json.results || [];
const objectives = Array.isArray(globalData.objectives?.daily) ? globalData.objectives.daily : [];
const persona = globalData.agentPersona || 'an AI operations chief of staff';
return [{ json: { summaryPrompt: `You are ${persona}. Draft a status update that summarizes these domain results (${JSON.stringify(results)}). Highlight blockers, upcoming deadlines, and next actions. Objectives: ${objectives.join(', ')}` } }];

const store = this.getWorkflowStaticData('global');
const sourceList = store.taskMap?.calendar || [];
return items.map((item, index) => {
  const paired = item.pairedItem?.item ?? index;
  const source = sourceList[paired] || item.json.task || {};
  if (item.json.result && item.json.domain) {
    return { json: { domain: item.json.domain, action: item.json.action, task: item.json.task, result: item.json.result } };
  }
  if (item.json.domain && item.json.result === undefined && !sourceList.length) {
    return { json: { domain: item.json.domain, action: item.json.action, task: item.json.task, result: item.json } };
  }
  return { json: { domain: source.domain || 'calendar', action: source.action, task: source, result: item.json } };
});

const globalData = this.getWorkflowStaticData('global');
if (!globalData.results) globalData.results = [];
for (const item of items) {
  const payload = item.json || {};
  if (payload.domain) {
    globalData.results.push(payload);
  }
}
return [{ json: { domain: 'calendar', processed: items.length } }];

