// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const utilities = {

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  // JSON Parser - Handles GPT-4o output with multiple formats
  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  parseModelJson(raw, label = 'model output') {
    if (raw && typeof raw === 'object') return raw;

    const text = String(raw || '').trim();
    if (!text) return {};

    // Try multiple extraction patterns
    const patterns = [
      /```json\s*([\s\S]*?)```/i,
      /```\s*([\s\S]*?)```/,
      /\{[\s\S]*\}/
    ];

    let jsonStr = text;
    for (const pattern of patterns) {
      const match = text.match(pattern);
      if (match) {
        jsonStr = match[1] || match[0];
        break;
      }
    }

    // Sanitize
    const sanitized = jsonStr
      .replace(/^\uFEFF/, '')
      .replace(/\r/g, '')
      .replace(/\\(?!["\\/ bfnrtu])/g, '\\\\');

    try {
      const parsed = JSON.parse(sanitized);
      if (!parsed || typeof parsed !== 'object') {
        throw new Error('Parsed result is not an object');
      }
      return parsed;
    } catch (error) {
      console.error(`âŒ JSON Parse Error [${label}]:`, error.message);
      console.error('Snippet:', sanitized.slice(0, 300));
      return {
        _parseError: true,
        _error: error.message,
        _label: label
      };
    }
  },

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  // Safe Node Accessor - Prevents workflow crashes
  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  safeNode(nodeName, defaultValue = {}) {
    try {
      const node = $(nodeName);
      if (!node || node.length === 0) return defaultValue;
      return node.first().json || defaultValue;
    } catch (error) {
      console.warn(`âš ï¸  Node ${nodeName} not accessible:`, error.message);
      return defaultValue;
    }
  },

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  // Metadata Wrapper - Adds audit tracking to all outputs
  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  addMetadata(data, context = {}) {
    return {
      ...data,
      _meta: {
        timestamp: new Date().toISOString(),
        workflow_id: typeof $workflow !== 'undefined' ? $workflow.id : 'unknown',
        execution_id: typeof $execution !== 'undefined' ? $execution.id : 'unknown',
        ...context
      }
    };
  },

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  // Validation Helper
  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  validate(data, rules, phaseName) {
    const errors = [];

    for (const [field, rule] of Object.entries(rules)) {
      const value = data[field];

      if (rule.required && !value) {
        errors.push(`Missing required field: ${field}`);
      }

      if (rule.minItems && Array.isArray(value) && value.length < rule.minItems) {
        errors.push(`${field} has ${value.length} items, minimum ${rule.minItems} required`);
      }

      if (rule.type && typeof value !== rule.type) {
        errors.push(`${field} should be type ${rule.type}, got ${typeof value}`);
      }
    }

    if (errors.length > 0) {
      throw new Error(`âŒ ${phaseName} VALIDATION FAILED:\n  - ${errors.join('\n  - ')}`);
    }

    return true;
  }
};

